import { useEffect, useState } from "react";
import { Typography, AppBar, CssBaseline, Grid, Container, Icon, Toolbar } from '@material-ui/core';

const fetchComments = async (postId) => {
  const response = await fetch(
    `https://jsonplaceholder.typicode.com/posts/${postId}/comments`
  );
  return response.json();
};

function Post({ title, body, postId }) {
  const [comments, setComments] = useState([]);

  const clickHandler = () => {
    fetchComments(postId).then(setComments);
  };

  return (
    <div>
      <h5>{postId}</h5>
      <h1 onClick={clickHandler}>{title}</h1>
      <p>{body}</p>
      {comments.length && (
        <>
          Comments:
          <ul>
            {comments.map(({ id, email, body }) => (
              <li key={id}>
                <dl>
                  <dt>{email}</dt>
                  <dd>{body}</dd>
                </dl>
              </li>
            ))}
          </ul>
        </>
      )}
    </div>
  );
}

function Posts() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const loadposts = async () => {
      const resp = await fetch(
        "https://jsonplaceholder.typicode.com/posts?userId=1"
      );
      const data = await resp.json();
      setPosts(data);
    };
    loadposts();
  }, []);

  return (
              <div>

                  <CssBaseline/>
                        <AppBar position ='relative'>
                        <Toolbar>
                            <Typography variant="h5">Posts List</Typography>
                        </Toolbar>
                        </AppBar>
                        <Typography variant="h2" align="center" color="textPrimary">
            Posts
        </Typography>
        <Typography variant="h6" align="center" color="textSecondary" paragraph>
            Display list of Posts and associated comments!!!
        </Typography>

                        <main>
                        <Container maxWidth="md">
                {posts.map((post) => (
          <li key={post.id}>
            <Post postId={post.id} title={post.title} body={post.body} />
          </li>
        ))}
      </Container>
      </main>
    </div>
  );
}

export default function App() {
  return (
    <div className="App">
      <Posts />
    </div>
  );
}
