import { Button } from '@mui/material';
import React from "react";
import './App.css';
class App extends React.Component {

	// Constructor
	constructor(props) {
		super(props);

		this.state = {
			items: [],
			DataisLoaded: false
		};
	}

	// ComponentDidMount is used to
	// execute the code
	componentDidMount() {
		fetch(
"https://jsonplaceholder.typicode.com/posts")
			.then((res) => res.json())
			.then((json) => {
				this.setState({
					items: json,
					DataisLoaded: true
				});
			})
	}
	render() {
		const { DataisLoaded, items } = this.state;
		if (!DataisLoaded) return <div>
			<h1> Pleses wait some time.... </h1> </div> ;

		return (
		<div className = "App">
			<h1> Take Home Assessment </h1>
      <div>{
				items.map((item) => (
				<ol key = { item.id } >
					Title: { item.title },
          <br></br>
					Description: { item.body }

					</ol>
				))
			}
      </div> 
		</div>
	);
}
}

export default App;
